#include <stdio.h>
#include "linkedlist.h"
#include "alignment.h"
#define BITAP_LEN 10000

int main(){
    list *T = make_new_list();
    list *S = make_new_list();
    int bitap_ret[BITAP_LEN];
    int j;
    T = insert_data(T, "", -1);
    S = insert_data(S, "cbaacabbaaaacccbcbaabcaba", -1);

    for (j = 0; j < BITAP_LEN; j++)
    {
        bitap_ret[j] = -1;
    }

    bitap(S, T, 1, bitap_ret, BITAP_LEN);
    for (j = 0; j < BITAP_LEN; j++)
    {
        if(bitap_ret[j] == -1){
            break;
        }
        printf("%d, ", bitap_ret[j]);
    }
    printf("\n ");

}